cle, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 25%, rgba(255,255,255,1) 25%, rgba(255,255,255,1) 100%);\r\n}\r\n`;\r\nlet string2 = ``;\r\nlet n = 0;\r\n\r\nlet step = () =>{\r\n    setTimeout(() =>{\r\n        if(string[n] === \"\\n\"){\r\n            string2 += \"<br>\";\r\n            // 如果是回车，就不照搬\r\n        }else if(string[n] === \" \"){\r\n            string2 += \"&nbsp\";\r\n        }else{\r\n            string2 += string[n];\r\n            // 如果不是回车，就照搬\r\n        }\r\n        html.innerHTML = string2;\r\n        style.innerHTML = string.substring(0, n);\r\n        window.scrollTo(0,99999);\r\n        html.scrollTo(0,99999);\r\n        if(n < string.length-1){\r\n            // 如果n不是最后一个，就继续\r\n            n += 1;     //54\r\n            step();\r\n        }\r\n    },10);\r\n};\r\n\r\nstep();\r\n\r\n\r\n// setTimeout(() =>{\r\n//     style.innerHTML = `\r\n//     body{\r\n//         color: red;\r\n//     }\r\n//     `;\r\n// },3000);\r\n\r\n\r\n\r\n   \r\n"],"names":[],"version":3,"file":"index.de158e3a.js.map","sourceRoot":"/__parcel_source_root/"}